import React, { useRef, useEffect, useState } from 'react';
import './App.css';

const GRAVITY = 0.7;
const ENERGY_LOSS = 0.7;
const BOOST = 20;
const BALL_SIZE = 80;

const App: React.FC = () => {
  const [isBouncing, setIsBouncing] = useState(false);
  const sphereRef = useRef<HTMLDivElement>(null);
  const velocityRef = useRef(0);
  const centerRef = useRef(window.innerHeight / 2 - BALL_SIZE / 2);
  const positionRef = useRef(centerRef.current);
  const animationRef = useRef<number>();
  const boundsRef = useRef({
    top: 0,
    center: centerRef.current,
  });

  // Update bounds on resize
  useEffect(() => {
    const handleResize = () => {
      centerRef.current = window.innerHeight / 2 - BALL_SIZE / 2;
      boundsRef.current = {
        top: 0,
        center: centerRef.current,
      };
      positionRef.current = centerRef.current;
      if (sphereRef.current) {
        sphereRef.current.style.top = `${positionRef.current}px`;
      }
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Start in middle
  useEffect(() => {
    if (sphereRef.current) {
      sphereRef.current.style.top = `${positionRef.current}px`;
    }
  }, []);

  // Bounce logic
  const animate = () => {
    animationRef.current = requestAnimationFrame(() => {
      velocityRef.current -= GRAVITY;
      positionRef.current -= velocityRef.current;

      // Bounce off the top
      if (positionRef.current < boundsRef.current.top) {
        positionRef.current = boundsRef.current.top;
        velocityRef.current = -velocityRef.current * ENERGY_LOSS;
      }

      // Bounce off the center (never below center)
      if (positionRef.current > boundsRef.current.center) {
        positionRef.current = boundsRef.current.center;
        velocityRef.current = -velocityRef.current * ENERGY_LOSS;
        // Stop if velocity is very low
        if (Math.abs(velocityRef.current) < 1.5) {
          velocityRef.current = 0;
          setIsBouncing(false);
          if (sphereRef.current) {
            sphereRef.current.style.top = `${positionRef.current}px`;
          }
          return;
        }
      }

      if (sphereRef.current) {
        sphereRef.current.style.top = `${positionRef.current}px`;
      }

      animate();
    });
  };

  // Always set velocity to BOOST on click
  const handleSphereClick = () => {
    velocityRef.current = BOOST;
    if (!isBouncing) {
      setIsBouncing(true);
      animate();
    }
  };

  // Cleanup animation frame
  useEffect(() => {
    return () => {
      if (animationRef.current) cancelAnimationFrame(animationRef.current);
    };
  }, []);

  return (
    <div style={{ position: 'fixed', inset: 0, overflow: 'hidden' }}>
      <div
        ref={sphereRef}
        className="sphere"
        style={{
          position: 'absolute',
          left: `calc(50% - ${BALL_SIZE / 2}px)`,
          top: `${centerRef.current}px`,
          cursor: 'pointer',
        }}
        onClick={handleSphereClick}
      />
    </div>
  );
};

export default App;
