import React, { useRef, useEffect, useState } from 'react';
import './App.css';

const GRAVITY = 0.7;
const ENERGY_LOSS = 0.7;
const BOOST = 18;
const FLOOR = 0;

const App: React.FC = () => {
  const [showSphere, setShowSphere] = useState(false);
  const [isBouncing, setIsBouncing] = useState(false);
  const sphereRef = useRef<HTMLDivElement>(null);
  const velocityRef = useRef(0);
  const positionRef = useRef(0);
  const animationRef = useRef<number>();

  useEffect(() => {
    if (showSphere && !isBouncing) {
      velocityRef.current = BOOST;
      positionRef.current = FLOOR;
      setIsBouncing(true);
      animate();
    }
    // eslint-disable-next-line
  }, [showSphere]);

  const animate = () => {
    animationRef.current = requestAnimationFrame(() => {
      velocityRef.current -= GRAVITY;
      positionRef.current += velocityRef.current;

      if (positionRef.current < FLOOR) {
        positionRef.current = FLOOR;
        velocityRef.current = -velocityRef.current * ENERGY_LOSS;

        if (Math.abs(velocityRef.current) < 1.5) {
          velocityRef.current = 0;
          setIsBouncing(false);
          return;
        }
      }

      if (sphereRef.current) {
        sphereRef.current.style.transform = `translateY(${-positionRef.current}px)`;
      }

      animate();
    });
  };

  const handleBounce = () => {
    setShowSphere(true);
    velocityRef.current += BOOST;
    if (!isBouncing) {
      setIsBouncing(true);
      animate();
    }
  };

  useEffect(() => {
    return () => {
      if (animationRef.current) cancelAnimationFrame(animationRef.current);
    };
  }, []);

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <button onClick={handleBounce}>Bounce Sphere</button>
      <div style={{ height: '200px', position: 'relative', marginTop: '40px' }}>
        {showSphere && (
          <div
            ref={sphereRef}
            className="sphere"
            style={{
              position: 'absolute',
              left: '50%',
              transform: 'translate(-50%, 0)',
              bottom: 0,
            }}
          />
        )}
      </div>
    </div>
  );
};

export default App;
