import React, { useRef, useEffect, useState } from "react";

const BALL_SIZE = 80;
const HITBOX_SIZE = BALL_SIZE + 20;
const GRAVITY = 1.6;
const ENERGY_LOSS = 0.7;
const BOOST = 30;
const LASER_WIDTH = 25;
const LASER_HEIGHT = 2;
const BASE_LASER_SPEED = 10;
const LASER_SPEED_INCREMENT = 2;
const MAX_HOLD_TIME = 1000; // Max hold duration (ms)
const BOOST_HOLD_INCREMENT = 0.15; // Continuous boost while holding

const App: React.FC = () => {
  const [score, setScore] = useState(0);

  const sphereRef = useRef<HTMLDivElement>(null);
  const lasersRef = useRef<HTMLDivElement[]>([]);
  const numLasers = useRef(1);

  const centerY = useRef(window.innerHeight / 2 - HITBOX_SIZE / 2);
  const spherePos = useRef({
    x: window.innerWidth / 2 - BALL_SIZE / 2,
    y: centerY.current,
  });
  const sphereVelocity = useRef(0);

  const laserData = useRef<
    { x: number; y: number; hit: boolean; scored: boolean }[]
  >([{ x: window.innerWidth, y: centerY.current - 20, hit: false, scored: false }]);

  const hasJumped = useRef(false);
  const isHolding = useRef(false);
  const holdStartTime = useRef(0);
  const scoreRef = useRef(0);

  useEffect(() => {
    const interval = setInterval(() => setScore(scoreRef.current), 200);
    return () => clearInterval(interval);
  }, []);

  // Handle mouse or key press
  const startHold = () => {
    if (!hasJumped.current) {
      sphereVelocity.current = BOOST * 0.8; // Slightly slower initial boost
      hasJumped.current = true;
      isHolding.current = true;
      holdStartTime.current = Date.now();
    }
  };

  const endHold = () => {
    isHolding.current = false;
  };

  useEffect(() => {
    const loop = () => {
      // Determine how many lasers to show
      if (scoreRef.current >= 100) numLasers.current = 3;
      else if (scoreRef.current >= 50) numLasers.current = 2;
      else numLasers.current = 1;

      // Speed logic with reset every 50 points
      const sectionScore = scoreRef.current % 50;
      const currentLaserSpeed =
        BASE_LASER_SPEED + LASER_SPEED_INCREMENT * Math.floor(sectionScore / 10);

      // Apply gravity
      if (isHolding.current) {
        const holdDuration = Date.now() - holdStartTime.current;
        if (holdDuration < MAX_HOLD_TIME) {
          sphereVelocity.current += BOOST_HOLD_INCREMENT; // small continuous lift
        } else {
          isHolding.current = false;
        }
      } else {
        sphereVelocity.current -= GRAVITY;
      }

      spherePos.current.y -= sphereVelocity.current;

      // Top boundary
      if (spherePos.current.y < 0) {
        spherePos.current.y = 0;
        sphereVelocity.current = -sphereVelocity.current * ENERGY_LOSS;
      }

      // Ground collision
      if (spherePos.current.y > centerY.current) {
        spherePos.current.y = centerY.current;
        sphereVelocity.current = 0;
        hasJumped.current = false; // ✅ can jump again immediately after landing
      }

      // Adjust laser count
      while (laserData.current.length < numLasers.current) {
        const offset = (window.innerWidth / numLasers.current) * laserData.current.length;
        laserData.current.push({
          x: window.innerWidth + offset,
          y: centerY.current - 20,
          hit: false,
          scored: false,
        });
      }
      while (laserData.current.length > numLasers.current) {
        laserData.current.pop();
      }

      // Update lasers
      laserData.current.forEach((laser, i) => {
        const s = spherePos.current;
        laser.x -= currentLaserSpeed;

        // Collision
        if (
          !laser.hit &&
          s.x + BALL_SIZE > laser.x &&
          s.x < laser.x + LASER_WIDTH &&
          s.y + BALL_SIZE > laser.y &&
          s.y < laser.y + LASER_HEIGHT
        ) {
          laser.hit = true;
          if (sphereRef.current) sphereRef.current.style.backgroundColor = "red";
        }

        // Scoring
        if (hasJumped.current && !laser.scored && !laser.hit && laser.x + LASER_WIDTH < s.x) {
          scoreRef.current += 1;
          laser.scored = true;
          if (sphereRef.current) sphereRef.current.style.backgroundColor = "#4fc3f7";
        }

        // Reset laser when off screen
        if (laser.x + LASER_WIDTH < 0) {
          laser.x = window.innerWidth;
          laser.hit = false;
          laser.scored = false;
        }

        // Apply transform
        if (lasersRef.current[i]) {
          lasersRef.current[i].style.transform = `translate3d(${laser.x}px, ${laser.y}px, 0)`;
        }
      });

      // Update sphere position
      if (sphereRef.current)
        sphereRef.current.style.transform = `translate3d(${spherePos.current.x}px, ${spherePos.current.y}px, 0)`;

      requestAnimationFrame(loop);
    };

    requestAnimationFrame(loop);
  }, []);

  useEffect(() => {
    const handleResize = () => {
      centerY.current = window.innerHeight / 2 - HITBOX_SIZE / 2;
      spherePos.current.y = centerY.current;
      laserData.current.forEach((laser) => (laser.y = centerY.current - 20));
    };
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Keyboard controls
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.code === "Space") startHold();
    };
    const handleKeyUp = (e: KeyboardEvent) => {
      if (e.code === "Space") endHold();
    };
    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, []);

  return (
    <div
      style={{ position: "fixed", inset: 0, overflow: "hidden" }}
      onMouseDown={startHold}
      onMouseUp={endHold}
      onTouchStart={startHold}
      onTouchEnd={endHold}
    >
      <div
        style={{
          position: "absolute",
          top: 20,
          width: "100%",
          textAlign: "center",
          fontSize: "2rem",
          fontWeight: "bold",
        }}
      >
        Score: {score}
      </div>

      <div
        ref={sphereRef}
        style={{
          position: "absolute",
          width: `${BALL_SIZE}px`,
          height: `${BALL_SIZE}px`,
          borderRadius: "50%",
          backgroundColor: "#4fc3f7",
        }}
      />

      {Array.from({ length: numLasers.current }).map((_, i) => (
        <div
          key={i}
          ref={(el) => {
            if (el) lasersRef.current[i] = el;
          }}
          style={{
            position: "absolute",
            width: `${LASER_WIDTH}px`,
            height: `${LASER_HEIGHT}px`,
            backgroundColor: "red",
            boxShadow: "0 0 10px red",
          }}
        />
      ))}
    </div>
  );
};

export default App;
