import React, { useRef, useEffect, useState } from 'react';
import './App.css';

const GRAVITY = 0.7;
const ENERGY_LOSS = 0.7;
const BOOST = 18;

const App: React.FC = () => {
  const [showSphere, setShowSphere] = useState(true);
  const [isBouncing, setIsBouncing] = useState(false);
  const sphereRef = useRef<HTMLDivElement>(null);
  const velocityRef = useRef(BOOST);
  const positionRef = useRef(0);
  const animationRef = useRef<number>();
  const windowHeightRef = useRef(window.innerHeight - 200); // 200px for container

  useEffect(() => {
    const handleResize = () => {
      windowHeightRef.current = window.innerHeight - 200;
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    if (showSphere && !isBouncing) {
      setIsBouncing(true);
      animate();
    }
    // eslint-disable-next-line
  }, [showSphere]);

  const animate = () => {
    animationRef.current = requestAnimationFrame(() => {
      velocityRef.current -= GRAVITY;
      positionRef.current += velocityRef.current;

      // Bounce off the floor
      if (positionRef.current < 0) {
        positionRef.current = 0;
        velocityRef.current = -velocityRef.current * ENERGY_LOSS;
        if (Math.abs(velocityRef.current) < 1.5) {
          velocityRef.current = 0;
          setIsBouncing(false);
          return;
        }
      }

      // Bounce off the top
      if (positionRef.current > windowHeightRef.current) {
        positionRef.current = windowHeightRef.current;
        velocityRef.current = -velocityRef.current * ENERGY_LOSS;
      }

      if (sphereRef.current) {
        sphereRef.current.style.transform = `translateY(${-positionRef.current}px)`;
      }

      animate();
    });
  };

  const handleSphereClick = () => {
    velocityRef.current += BOOST;
    if (!isBouncing) {
      setIsBouncing(true);
      animate();
    }
  };

  useEffect(() => {
    return () => {
      if (animationRef.current) cancelAnimationFrame(animationRef.current);
    };
  }, []);

  return (
    <div style={{ height: '200px', position: 'relative', marginTop: '40px', textAlign: 'center' }}>
      {showSphere && (
        <div
          ref={sphereRef}
          className="sphere"
          style={{
            position: 'absolute',
            left: '50%',
            transform: 'translate(-50%, 0)',
            bottom: 0,
            cursor: 'pointer',
          }}
          onClick={handleSphereClick}
        />
      )}
    </div>
  );
};

export default App;
