    <div style={{ position: 'fixed', inset: 0, overflow: 'hidden', background: '#000' }}>
      <canvas ref={canvasRef} width={window.innerWidth} height={window.innerHeight} style={{ position: 'absolute', inset: 0 }} />

      {!gameOver && (
        <>
          <div style={{ position: 'absolute', top: 20, width: '100%', textAlign: 'center', fontSize: '2rem', fontWeight: 'bold', color: 'white' }}>
            Score: {score}
          </div>

          <div
            ref={sphereRef}
            style={{
              position: 'absolute',
              width: `${BALL_SIZE}px`,
              height: `${BALL_SIZE}px`,
              borderRadius: '50%',
              backgroundColor: '#4fc3f7',
              transform: 'scale(1,1)',
            }}
          />

          {Array.from({ length: numLasers.current }).map((_, i) => (
            <div
              key={i}
              ref={(el) => { if (el) lasersRef.current[i] = el; }}
              style={{ position: 'absolute', width: `${LASER_WIDTH}px`, height: `${LASER_HEIGHT}px`, backgroundColor: 'red', boxShadow: '0 0 10px red' }}
            />
          ))}
        </>
      )}

      {gameOver && (
        <div
          onClick={handleRestart}
          style={{
            position: 'absolute',
            inset: 0,
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            color: 'white',
            fontSize: '8rem',
            fontWeight: 'bold',
            cursor: 'pointer',
          }}
        >
          JUMP!
        </div>
      )}
    </div>
  );
};

export default App;
