import React, { useState } from 'react';
import './App.css';

const App: React.FC = () => {
  const [showSphere, setShowSphere] = useState(false);

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <button onClick={() => setShowSphere(true)}>Show Bouncing Sphere</button>
      {showSphere && <div className="sphere" />}
    </div>
  );
};

export default App;
