/**
 * Enemy Component
 * Renders the enemy launcher ball with squash/stretch morphing effect
 */

import React from 'react';
import { BALL_SIZE } from '../config/gameConfig';

interface EnemyProps {
  x: number;
  y: number;
  scaleX?: number;
  scaleY?: number;
  isHit?: boolean; // Whether enemy was just hit by player projectile
}

export const Enemy = React.forwardRef<HTMLDivElement, EnemyProps>(
  ({ x, y, scaleX = 1, scaleY = 1, isHit = false }, ref) => {
    return (
      <div
        ref={ref}
        style={{
          position: 'absolute',
          width: `${BALL_SIZE}px`,
          height: `${BALL_SIZE}px`,
          borderRadius: '50%',
          backgroundColor: isHit ? '#4fc3f7' : 'red', // Change to blue when hit
          boxShadow: isHit ? '0 0 15px #4fc3f7' : '0 0 15px red',
          transform: `translate3d(${x}px, ${y}px, 0) scale(${scaleX}, ${scaleY})`,
          transition: 'transform 0.3s ease-out, background-color 0.25s ease, box-shadow 0.25s ease',
        }}
      />
    );
  }
);

Enemy.displayName = 'Enemy';
