import React, { useRef, useEffect, useState } from 'react';

const BALL_SIZE = 80;
const HITBOX_SIZE = BALL_SIZE + 20;
const GRAVITY = 1.6;
const ENERGY_LOSS = 0.7;
const BOOST = 30;
const LASER_WIDTH = 25;
const LASER_HEIGHT = 2;
const BASE_LASER_SPEED = 10;
const LASER_SPEED_INCREMENT = 2;

const App: React.FC = () => {
  const [score, setScore] = useState(0);

  const sphereRef = useRef<HTMLDivElement>(null);
  const lasersRef = useRef<HTMLDivElement[]>([]);
  const numLasers = useRef(1);

  const centerY = useRef(window.innerHeight / 2 - HITBOX_SIZE / 2);
  const spherePos = useRef({ x: window.innerWidth / 2 - BALL_SIZE / 2, y: centerY.current });
  const sphereVelocity = useRef(0);

  const laserPositions = useRef<{ x: number; y: number }[]>([
    { x: window.innerWidth, y: centerY.current - 20 },
  ]);

  const hasJumped = useRef(false);
  const scored = useRef(false);
  const scoreRef = useRef(0);

  useEffect(() => {
    const interval = setInterval(() => setScore(scoreRef.current), 200);
    return () => clearInterval(interval);
  }, []);

  const handleClick = () => {
    sphereVelocity.current = BOOST;
    hasJumped.current = true;
    scored.current = false;
  };

  useEffect(() => {
    const loop = () => {
      // Laser count management
      if (scoreRef.current >= 100) numLasers.current = 3;
      else if (scoreRef.current >= 50) numLasers.current = 2;
      else numLasers.current = 1;

      // Reset base speed every 50 points
      const sectionBase = Math.floor(scoreRef.current / 50);
      const sectionScore = scoreRef.current % 50;
      const currentLaserSpeed =
        BASE_LASER_SPEED + LASER_SPEED_INCREMENT * Math.floor(sectionScore / 10);

      // Ball physics
      sphereVelocity.current -= GRAVITY;
      spherePos.current.y -= sphereVelocity.current;

      if (spherePos.current.y < 0) {
        spherePos.current.y = 0;
        sphereVelocity.current = -sphereVelocity.current * ENERGY_LOSS;
      }

      if (spherePos.current.y > centerY.current) {
        spherePos.current.y = centerY.current;
        sphereVelocity.current = -sphereVelocity.current * ENERGY_LOSS;
        if (Math.abs(sphereVelocity.current) < 1.5) {
          sphereVelocity.current = 0;
          hasJumped.current = false;
          scored.current = false;
        }
      }

      // Ensure correct number of lasers exist
      while (laserPositions.current.length < numLasers.current) {
        const offset = (window.innerWidth / numLasers.current) * laserPositions.current.length;
        laserPositions.current.push({
          x: window.innerWidth + offset,
          y: centerY.current - 20,
        });
      }

      while (laserPositions.current.length > numLasers.current) {
        laserPositions.current.pop();
      }

      // Move & draw lasers
      laserPositions.current.forEach((l, i) => {
        l.x -= currentLaserSpeed;

        // Collision
        const s = spherePos.current;
        if (
          s.x + BALL_SIZE > l.x &&
          s.x < l.x + LASER_WIDTH &&
          s.y + BALL_SIZE > l.y &&
          s.y < l.y + LASER_HEIGHT
        ) {
          if (sphereRef.current) sphereRef.current.style.backgroundColor = 'red';
        }

        // Scoring (once per laser pass)
        if (hasJumped.current && !scored.current && l.x + LASER_WIDTH < s.x) {
          scoreRef.current += 1;
          scored.current = true;
          if (sphereRef.current) sphereRef.current.style.backgroundColor = '#4fc3f7';
        }

        // Reset laser
        if (l.x + LASER_WIDTH < 0) {
          l.x = window.innerWidth;
        }

        if (lasersRef.current[i]) {
          lasersRef.current[i].style.transform = `translate3d(${l.x}px, ${l.y}px, 0)`;
        }
      });

      // Apply sphere transform
      if (sphereRef.current)
        sphereRef.current.style.transform = `translate3d(${spherePos.current.x}px, ${spherePos.current.y}px, 0)`;

      requestAnimationFrame(loop);
    };
    requestAnimationFrame(loop);
  }, []);

  useEffect(() => {
    const handleResize = () => {
      centerY.current = window.innerHeight / 2 - HITBOX_SIZE / 2;
      spherePos.current.y = centerY.current;
      laserPositions.current.forEach((l) => (l.y = centerY.current - 20));
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div style={{ position: 'fixed', inset: 0, overflow: 'hidden' }} onClick={handleClick}>
      <div
        style={{
          position: 'absolute',
          top: 20,
          width: '100%',
          textAlign: 'center',
          fontSize: '2rem',
          fontWeight: 'bold',
        }}
      >
        Score: {score}
      </div>

      <div
        ref={sphereRef}
        style={{
          position: 'absolute',
          width: `${BALL_SIZE}px`,
          height: `${BALL_SIZE}px`,
          borderRadius: '50%',
          backgroundColor: '#4fc3f7',
        }}
      />

      {Array.from({ length: numLasers.current }).map((_, i) => (
        <div
          key={i}
          ref={(el) => {
            if (el) lasersRef.current[i] = el;
          }}
          style={{
            position: 'absolute',
            width: `${LASER_WIDTH}px`,
            height: `${LASER_HEIGHT}px`,
            backgroundColor: 'red',
            boxShadow: '0 0 10px red',
          }}
        />
      ))}
    </div>
  );
};

export default App;
