import React, { useRef, useEffect, useState } from 'react';

const BALL_SIZE = 80;
const HITBOX_SIZE = BALL_SIZE + 20;
const GRAVITY = 1.6;
const ENERGY_LOSS = 0.7;
const BOOST = 30;
const LASER_WIDTH = 25;
const LASER_HEIGHT = 2;
const BASE_LASER_SPEED = 10;
const LASER_SPEED_INCREMENT = 2;
const LASER_Y_OFFSET = 20;

function getLaserCount(score: number) {
  return Math.floor(score / 50) + 1;
}

function getLaserSpeed(score: number) {
  // Reset speed at each 50-point milestone
  if (score % 50 === 0 && score !== 0) return BASE_LASER_SPEED;
  // Otherwise, increase speed every 10 points
  return BASE_LASER_SPEED + LASER_SPEED_INCREMENT * Math.floor((score % 50) / 10);
}

const App: React.FC = () => {
  const scoreRef = useRef(0);
  const [score, setScore] = useState(0);

  const sphereRef = useRef<HTMLDivElement>(null);

  const centerY = useRef(window.innerHeight / 2 - HITBOX_SIZE / 2);
  const spherePos = useRef({ x: window.innerWidth / 2 - BALL_SIZE / 2, y: centerY.current });
  const sphereVelocity = useRef(0);

  // Lazers: array of lazers, each with x, y, hit, scored
  const lazersRef = useRef<
    {
      x: number;
      y: number;
      hit: boolean;
      scored: boolean;
    }[]
  >([
    {
      x: window.innerWidth,
      y: centerY.current - LASER_Y_OFFSET,
      hit: false,
      scored: false,
    },
  ]);

  const hasJumped = useRef(false);

  // Sync score every 200ms
  useEffect(() => {
    const interval = setInterval(() => setScore(scoreRef.current), 200);
    return () => clearInterval(interval);
  }, []);

  const handleClick = () => {
    sphereVelocity.current = BOOST;
    hasJumped.current = true;
    lazersRef.current.forEach(l => (l.scored = false));
  };

  // Animation loop
  useEffect(() => {
    let animationFrame: number;
    const loop = () => {
      // Lazers setup
      const laserCount = getLaserCount(scoreRef.current);
      const spacing = window.innerWidth / laserCount;

      // If laser count changed, re-initialize all lasers off-screen to the right, spaced evenly
      if (lazersRef.current.length !== laserCount) {
        lazersRef.current = Array.from({ length: laserCount }).map((_, i) => ({
          x: window.innerWidth + spacing * i,
          y: centerY.current - LASER_Y_OFFSET,
          hit: false,
          scored: false,
        }));
      }

      // Ball physics
      sphereVelocity.current -= GRAVITY;
      spherePos.current.y -= sphereVelocity.current;

      if (spherePos.current.y < 0) {
        spherePos.current.y = 0;
        sphereVelocity.current = -sphereVelocity.current * ENERGY_LOSS;
      }

      if (spherePos.current.y > centerY.current) {
        spherePos.current.y = centerY.current;
        sphereVelocity.current = -sphereVelocity.current * ENERGY_LOSS;
        if (Math.abs(sphereVelocity.current) < 1.5) {
          sphereVelocity.current = 0;
          hasJumped.current = false;
          lazersRef.current.forEach(l => (l.scored = false));
        }
      }

      // Move sphere
      if (sphereRef.current) {
        sphereRef.current.style.transform = `translate3d(${spherePos.current.x}px, ${spherePos.current.y}px, 0)`;
      }

      // Laser speed logic
      const currentLaserSpeed = getLaserSpeed(scoreRef.current);

      // Move all lasers every frame
      lazersRef.current.forEach(l => {
        l.x -= currentLaserSpeed;
      });

      // Collision detection and scoring for each laser
      lazersRef.current.forEach((l, idx) => {
        const s = spherePos.current;
        if (
          !l.hit &&
          l.x + LASER_WIDTH > s.x &&
          l.x < s.x + BALL_SIZE &&
          s.y + BALL_SIZE > l.y &&
          s.y < l.y + LASER_HEIGHT
        ) {
          l.hit = true;
          if (sphereRef.current) sphereRef.current.style.background = 'radial-gradient(circle at 30% 30%, #ff5252 70%, #b71c1c 100%)';
        }

        // Scoring
        if (hasJumped.current && !l.hit && !l.scored && l.x + LASER_WIDTH < s.x) {
          scoreRef.current += 1;
          l.scored = true;
        }

        // Reset laser when it goes off the left edge
        if (l.x + LASER_WIDTH < 0) {
          l.x = window.innerWidth + spacing * idx;
          l.hit = false;
          l.scored = false;
          if (sphereRef.current) sphereRef.current.style.background = 'radial-gradient(circle at 30% 30%, #4fc3f7 70%, #0288d1 100%)';
        }
      });

      animationFrame = requestAnimationFrame(loop);
    };
    loop();
    return () => cancelAnimationFrame(animationFrame);
  }, []);

  // Handle resize
  useEffect(() => {
    const handleResize = () => {
      centerY.current = window.innerHeight / 2 - HITBOX_SIZE / 2;
      spherePos.current.y = centerY.current;
      lazersRef.current.forEach(l => {
        l.y = centerY.current - LASER_Y_OFFSET;
      });
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div style={{ position: 'fixed', inset: 0, overflow: 'hidden' }} onClick={handleClick}>
      <div style={{ position: 'absolute', top: 20, width: '100%', textAlign: 'center', fontSize: '2rem', fontWeight: 'bold' }}>
        Score: {score}
      </div>
      <div
        ref={sphereRef}
        style={{
          position: 'absolute',
          width: `${BALL_SIZE}px`,
          height: `${BALL_SIZE}px`,
          borderRadius: '50%',
          background: 'radial-gradient(circle at 30% 30%, #4fc3f7 70%, #0288d1 100%)',
          boxShadow: '0 8px 24px rgba(0,0,0,0.2)',
        }}
      />
      {lazersRef.current.map((l, idx) => (
        <div
          key={idx}
          style={{
            position: 'absolute',
            width: `${LASER_WIDTH}px`,
            height: `${LASER_HEIGHT}px`,
            background: 'red',
            boxShadow: '0 0 10px red',
            transform: `translate3d(${l.x}px, ${l.y}px, 0)`,
          }}
        />
      ))}
    </div>
  );
};

export default App;
