import React, { useEffect, useRef, useState } from "react";

const CANVAS_WIDTH = window.innerWidth;
const CANVAS_HEIGHT = window.innerHeight;
const BALL_SIZE = 40;
const LASER_WIDTH = 12;
const INITIAL_LASER_SPEED = 6;
const GRAVITY = 0.6;
const JUMP_STRENGTH = 14;

interface Laser {
  x: number;
  y: number;
  active: boolean;
}

export default function App() {
  const sphereRef = useRef<HTMLDivElement>(null);
  const redBallRef = useRef<HTMLDivElement>(null);
  const lasersRef = useRef<(HTMLDivElement | null)[]>([]);
  const spherePos = useRef({ x: CANVAS_WIDTH * 0.25, y: CANVAS_HEIGHT - 200 });
  const velocityY = useRef(0);
  const [score, setScore] = useState(0);
  const [lasers, setLasers] = useState<Laser[]>([]);
  const [isJumping, setIsJumping] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [bgMusic] = useState(() => new Audio("/audioTrack.mp3"));
  const bounceSound = useRef<HTMLAudioElement | null>(null);
  const lazerHitSound = useRef<HTMLAudioElement | null>(null);

  const [nextLaserY, setNextLaserY] = useState(CANVAS_HEIGHT * 0.5);
  const [redBallY, setRedBallY] = useState(CANVAS_HEIGHT * 0.5);

  // Fade-out helper
  const fadeOutAudio = (audio: HTMLAudioElement) => {
    let volume = audio.volume;
    const fade = setInterval(() => {
      volume -= 0.05;
      if (volume <= 0) {
        clearInterval(fade);
        audio.pause();
        audio.currentTime = 0;
      } else {
        audio.volume = volume;
      }
    }, 50);
  };

  useEffect(() => {
    bgMusic.loop = true;
    bgMusic.volume = 0.2;
    bgMusic.play().catch(() => {});
  }, [bgMusic]);

  // Predict next laser position
  const getNextLaserY = () => {
    return Math.random() * (CANVAS_HEIGHT * 0.5);
  };

  // Smoothly move red ball toward next laser position
  useEffect(() => {
    const anim = setInterval(() => {
      setRedBallY((prev) => prev + (nextLaserY - prev) * 0.1);
    }, 30);
    return () => clearInterval(anim);
  }, [nextLaserY]);

  // Handle jump
  const handleJump = () => {
    if (gameOver) {
      resetGame();
      return;
    }

    if (!isJumping) {
      setIsJumping(true);
      velocityY.current = -JUMP_STRENGTH;

      if (bounceSound.current) {
        const snd = bounceSound.current.cloneNode() as HTMLAudioElement;
        snd.volume = 0.7;
        snd.play();
      }
    }
  };

  const resetGame = () => {
    setScore(0);
    setGameOver(false);
    setLasers([]);
    spherePos.current.y = CANVAS_HEIGHT - 200;
    velocityY.current = 0;
  };

  // Physics loop
  useEffect(() => {
    bounceSound.current = new Audio("/bounce.mp3");
    lazerHitSound.current = new Audio("/lazerHit.mp3");

    let laserSpeed = INITIAL_LASER_SPEED;
    let animationFrame: number;

    const loop = () => {
      velocityY.current += GRAVITY;
      spherePos.current.y += velocityY.current;

      if (spherePos.current.y > CANVAS_HEIGHT - BALL_SIZE - 100) {
        spherePos.current.y = CANVAS_HEIGHT - BALL_SIZE - 100;
        velocityY.current = 0;
        setIsJumping(false);
      }

      // Move lasers
      setLasers((prev) =>
        prev
          .map((laser) => ({ ...laser, x: laser.x - laserSpeed }))
          .filter((laser) => laser.x > -LASER_WIDTH)
      );

      // Add new laser if needed
      if (lasers.length === 0 || lasers[lasers.length - 1].x < CANVAS_WIDTH * 0.5) {
        const newY = getNextLaserY();
        setNextLaserY(newY);
        setLasers((prev) => [...prev, { x: CANVAS_WIDTH, y: newY, active: true }]);
      }

      // Collision check
      lasers.forEach((laser) => {
        if (
          laser.x < spherePos.current.x + BALL_SIZE &&
          laser.x + LASER_WIDTH > spherePos.current.x &&
          laser.y > spherePos.current.y - 60 &&
          laser.y < spherePos.current.y + BALL_SIZE
        ) {
          if (lazerHitSound.current) {
            lazerHitSound.current.currentTime = 0;
            lazerHitSound.current.volume = 0.7;
            lazerHitSound.current.play();
          }
          setGameOver(true);
        } else if (laser.x + LASER_WIDTH < spherePos.current.x && laser.active) {
          setScore((s) => s + 1);
          laser.active = false;
        }
      });

      // Difficulty scaling
      if (score > 0 && score % 25 === 0) {
        laserSpeed = INITIAL_LASER_SPEED;
      } else {
        laserSpeed += 0.0005;
      }

      animationFrame = requestAnimationFrame(loop);
    };

    animationFrame = requestAnimationFrame(loop);
    return () => cancelAnimationFrame(animationFrame);
  }, [score, lasers.length]);

  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        background: "radial-gradient(circle at center, #000010, #000)",
        overflow: "hidden",
        position: "relative",
      }}
      onMouseDown={handleJump}
      onTouchStart={handleJump}
    >
      {/* Player Ball */}
      <div
        ref={sphereRef}
        style={{
          position: "absolute",
          left: `${spherePos.current.x}px`,
          top: `${spherePos.current.y}px`,
          width: BALL_SIZE,
          height: BALL_SIZE,
          borderRadius: "50%",
          backgroundColor: "#4fc3f7",
          transition: "background-color 0.25s ease",
        }}
      />

      {/* Red Ball on the Right */}
      <div
        ref={redBallRef}
        style={{
          position: "absolute",
          left: `${CANVAS_WIDTH * 0.95}px`,
          top: `${redBallY}px`,
          width: BALL_SIZE,
          height: BALL_SIZE,
          borderRadius: "50%",
          backgroundColor: "#ff1744",
          transform: "translate(-50%, -50%)",
          transition: "top 0.3s ease",
        }}
      />

      {/* Lasers */}
      {lasers.map((laser, i) => (
        <div
          key={i}
          ref={(el) => (lasersRef.current[i] = el)}
          style={{
            position: "absolute",
            left: `${laser.x}px`,
            top: `${laser.y}px`,
            width: `${LASER_WIDTH}px`,
            height: "100px",
            backgroundColor: "#f50057",
            boxShadow: "0 0 10px #f50057, 0 0 20px #f50057",
          }}
        />
      ))}

      {/* Score */}
      <div
        style={{
          position: "absolute",
          top: 20,
          left: 20,
          fontSize: 28,
          fontWeight: "bold",
          color: "#fff",
        }}
      >
        Score: {score}
      </div>

      {gameOver && (
        <div
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            color: "#fff",
            fontSize: "120px",
            fontWeight: "bold",
          }}
        >
          JUMP!
        </div>
      )}
    </div>
  );
}
