import React, { useRef, useEffect, useState } from 'react';
import './App.css';

const GRAVITY = 1.2;
const ENERGY_LOSS = 0.7;
const BOOST = 24;
const BALL_SIZE = 80;
const HITBOX_SIZE = BALL_SIZE + 20;
const LASER_WIDTH = 25;
const LASER_HEIGHT = 1;
const LASER_SPEED = 12;

const App: React.FC = () => {
  const scoreRef = useRef(0);
  const [score, setScore] = useState(0);

  // Animation refs
  const sphereRef = useRef<HTMLDivElement>(null);
  const laserRef = useRef<HTMLDivElement>(null);

  const spherePos = useRef({ x: window.innerWidth / 2 - BALL_SIZE / 2, y: window.innerHeight / 2 - HITBOX_SIZE / 2 });
  const sphereVelocity = useRef(0);
  const laserPos = useRef({ x: window.innerWidth, y: window.innerHeight / 2 - HITBOX_SIZE / 2 - 20 });

  const hasJumpedRef = useRef(false);
  const laserHitRef = useRef(false);
  const scoredThisJumpRef = useRef(false);

  // Handle window resize
  useEffect(() => {
    const handleResize = () => {
      spherePos.current.y = window.innerHeight / 2 - HITBOX_SIZE / 2;
      laserPos.current.y = window.innerHeight / 2 - HITBOX_SIZE / 2 - 20;
      if (sphereRef.current) {
        sphereRef.current.style.transform = `translate(${spherePos.current.x}px, ${spherePos.current.y}px)`;
      }
      if (laserRef.current) {
        laserRef.current.style.transform = `translate(${laserPos.current.x}px, ${laserPos.current.y}px)`;
      }
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Bounce logic
  const animateBall = () => {
    sphereVelocity.current -= GRAVITY;
    spherePos.current.y -= sphereVelocity.current;

    // Bounce off the top
    if (spherePos.current.y < 0) {
      spherePos.current.y = 0;
      sphereVelocity.current = -sphereVelocity.current * ENERGY_LOSS;
    }

    // Bounce off the center (never below center)
    const centerY = window.innerHeight / 2 - HITBOX_SIZE / 2;
    if (spherePos.current.y > centerY) {
      spherePos.current.y = centerY;
      sphereVelocity.current = -sphereVelocity.current * ENERGY_LOSS;
      if (Math.abs(sphereVelocity.current) < 1.5) {
        sphereVelocity.current = 0;
        hasJumpedRef.current = false;
        scoredThisJumpRef.current = false;
      }
    }

    // Move sphere
    if (sphereRef.current) {
      sphereRef.current.style.transform = `translate(${spherePos.current.x}px, ${spherePos.current.y}px)`;
    }
  };

  // Laser logic
  const animateLaser = () => {
    laserPos.current.x -= LASER_SPEED;

    // Move laser
    if (laserRef.current) {
      laserRef.current.style.transform = `translate(${laserPos.current.x}px, ${laserPos.current.y}px)`;
    }

    // Collision detection (using JS positions, not DOM)
    const sphereLeft = spherePos.current.x;
    const sphereRight = spherePos.current.x + BALL_SIZE;
    const sphereTop = spherePos.current.y;
    const sphereBottom = spherePos.current.y + BALL_SIZE;

    const laserLeft = laserPos.current.x;
    const laserRight = laserPos.current.x + LASER_WIDTH;
    const laserTop = laserPos.current.y;
    const laserBottom = laserPos.current.y + LASER_HEIGHT;

    if (
      laserRight > sphereLeft &&
      laserLeft < sphereRight &&
      laserTop < sphereBottom &&
      laserBottom > sphereTop &&
      !laserHitRef.current
    ) {
      laserHitRef.current = true;
      if (sphereRef.current) {
        sphereRef.current.style.background = 'radial-gradient(circle at 30% 30%, #ff5252 70%, #b71c1c 100%)';
      }
    }

    // Score after successful jump (when laser passes sphere)
    if (
      hasJumpedRef.current &&
      !laserHitRef.current &&
      !scoredThisJumpRef.current &&
      laserRight < sphereLeft
    ) {
      setScore(s => s + 1);
      scoredThisJumpRef.current = true;
    }

    // Reset laser when off screen
    if (laserPos.current.x + LASER_WIDTH < 0) {
      laserPos.current.x = window.innerWidth;
      laserHitRef.current = false;
      scoredThisJumpRef.current = false;
      if (sphereRef.current) {
        sphereRef.current.style.background = 'radial-gradient(circle at 30% 30%, #4fc3f7 70%, #0288d1 100%)';
      }
    }
  };

  // Main animation loop
  useEffect(() => {
    let animationFrame: number;
    const loop = () => {
      animateBall();
      animateLaser();
      animationFrame = requestAnimationFrame(loop);
    };
    loop();
    return () => cancelAnimationFrame(animationFrame);
  }, []);

  // Handle jump
  const handleHitboxClick = () => {
    sphereVelocity.current = BOOST;
    hasJumpedRef.current = true;
    scoredThisJumpRef.current = false;
  };

  useEffect(() => {
    const interval = setInterval(() => setScore(scoreRef.current), 200);
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ position: 'fixed', inset: 0, overflow: 'hidden' }}>
      <div style={{
        position: 'absolute',
        top: 20,
        left: 0,
        width: '100%',
        textAlign: 'center',
        fontSize: '2rem',
        fontWeight: 'bold',
        color: '#333'
      }}>
        Score: {score}
      </div>
      <div
        className="hitbox"
        style={{
          position: 'absolute',
          left: 0,
          top: 0,
          width: '100vw',
          height: '100vh',
          cursor: 'pointer',
        }}
        onClick={handleHitboxClick}
      >
        <div
          ref={sphereRef}
          className="sphere"
          style={{
            position: 'absolute',
            width: `${BALL_SIZE}px`,
            height: `${BALL_SIZE}px`,
            background: 'radial-gradient(circle at 30% 30%, #4fc3f7 70%, #0288d1 100%)',
            borderRadius: '50%',
            boxShadow: '0 8px 24px rgba(0,0,0,0.2)',
            transition: 'background 0.2s',
            // REMOVE left/top, use only transform for position
          }}
        />
      </div>
      {/* Laser beam */}
      <div
        ref={laserRef}
        className="laser"
        style={{
          position: 'absolute',
          width: `${LASER_WIDTH}px`,
          height: `${LASER_HEIGHT}px`,
          background: 'red',
          boxShadow: '0 0 8px 2px red',
        }}
      />
    </div>
  );
};

export default App;
