import React, { useRef, useEffect, useState } from 'react';

const BALL_SIZE = 80;
const HITBOX_SIZE = BALL_SIZE + 20;
const GRAVITY = 1.6;
const ENERGY_LOSS = 0.7;
const BOOST = 30;
const LASER_WIDTH = 25;
const LASER_HEIGHT = 2;
const BASE_LASER_SPEED = 10;
const LASER_SPEED_INCREMENT = 2;

const App: React.FC = () => {
  const [score, setScore] = useState(0);

  const sphereRef = useRef<HTMLDivElement>(null);
  const laserRef = useRef<HTMLDivElement>(null);

  const centerY = useRef(window.innerHeight / 2 - HITBOX_SIZE / 2);
  const spherePos = useRef({ x: window.innerWidth / 2 - BALL_SIZE / 2, y: centerY.current });
  const sphereVelocity = useRef(0);
  const laserPos = useRef({ x: window.innerWidth, y: centerY.current - 20 });

  const hasJumped = useRef(false);
  const laserHit = useRef(false);
  const scored = useRef(false);
  const scoreRef = useRef(0);

  // Sync score every 200ms
  useEffect(() => {
    const interval = setInterval(() => setScore(scoreRef.current), 200);
    return () => clearInterval(interval);
  }, []);

  const handleClick = () => {
    sphereVelocity.current = BOOST;
    hasJumped.current = true;
    scored.current = false;
  };

  // Animation loop
  useEffect(() => {
    const loop = () => {
      // Calculate current laser speed based on score
      const currentLaserSpeed =
        BASE_LASER_SPEED + LASER_SPEED_INCREMENT * Math.floor(scoreRef.current / 10);

      // Ball physics
      sphereVelocity.current -= GRAVITY;
      spherePos.current.y -= sphereVelocity.current;

      if (spherePos.current.y < 0) {
        spherePos.current.y = 0;
        sphereVelocity.current = -sphereVelocity.current * ENERGY_LOSS;
      }

      if (spherePos.current.y > centerY.current) {
        spherePos.current.y = centerY.current;
        sphereVelocity.current = -sphereVelocity.current * ENERGY_LOSS;
        if (Math.abs(sphereVelocity.current) < 1.5) {
          sphereVelocity.current = 0;
          hasJumped.current = false;
          scored.current = false;
        }
      }

      // Laser physics
      laserPos.current.x -= currentLaserSpeed;

      // Collision detection (JS positions only)
      const s = spherePos.current;
      const l = laserPos.current;
      if (
        !laserHit.current &&
        s.x + BALL_SIZE > l.x &&
        s.x < l.x + LASER_WIDTH &&
        s.y + BALL_SIZE > l.y &&
        s.y < l.y + LASER_HEIGHT
      ) {
        laserHit.current = true;
        if (sphereRef.current) sphereRef.current.style.backgroundColor = 'red';
      }

      // Scoring
      if (hasJumped.current && !laserHit.current && !scored.current && l.x + LASER_WIDTH < s.x) {
        scoreRef.current += 1;
        scored.current = true;
      }

      // Reset laser
      if (l.x + LASER_WIDTH < 0) {
        l.x = window.innerWidth;
        laserHit.current = false;
        scored.current = false;
        if (sphereRef.current) sphereRef.current.style.backgroundColor = '#4fc3f7';
      }

      // Apply positions
      if (sphereRef.current) sphereRef.current.style.transform = `translate3d(${s.x}px, ${s.y}px, 0)`;
      if (laserRef.current) laserRef.current.style.transform = `translate3d(${l.x}px, ${l.y}px, 0)`;

      requestAnimationFrame(loop);
    };
    requestAnimationFrame(loop);
  }, []);

  // Handle resize
  useEffect(() => {
    const handleResize = () => {
      centerY.current = window.innerHeight / 2 - HITBOX_SIZE / 2;
      spherePos.current.y = centerY.current;
      laserPos.current.y = centerY.current - 20;
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div style={{ position: 'fixed', inset: 0, overflow: 'hidden' }} onClick={handleClick}>
      <div style={{ position: 'absolute', top: 20, width: '100%', textAlign: 'center', fontSize: '2rem', fontWeight: 'bold' }}>
        Score: {score}
      </div>
      <div
        ref={sphereRef}
        style={{
          position: 'absolute',
          width: `${BALL_SIZE}px`,
          height: `${BALL_SIZE}px`,
          borderRadius: '50%',
          backgroundColor: '#4fc3f7',
        }}
      />
      <div
        ref={laserRef}
        style={{
          position: 'absolute',
          width: `${LASER_WIDTH}px`,
          height: `${LASER_HEIGHT}px`,
          backgroundColor: 'red',
          boxShadow: '0 0 10px red',
        }}
      />
    </div>
  );
};

export default App;
