import React, { useRef, useEffect, useState } from 'react';

const BALL_SIZE = 80;
const HITBOX_SIZE = BALL_SIZE + 20;

// Slowed down physics
const GRAVITY = 0.3;           // Reduced gravity for slower fall
const ENERGY_LOSS = 0.6;       // Slightly softer bounce
const BOOST = 15;              // Slower initial jump
const HOLD_BOOST = 0.10;        // Gentle upward push while holding
const MAX_HOLD_TIME = 2000;    // Hold can last 1 second

// Slower lasers
const LASER_WIDTH = 25;
const LASER_HEIGHT = 2;
const BASE_LASER_SPEED = 4;    // Reduced from 10
const LASER_SPEED_INCREMENT = 0.4;

const App: React.FC = () => {
  const [score, setScore] = useState(0);

  const sphereRef = useRef<HTMLDivElement>(null);
  const lasersRef = useRef<HTMLDivElement[]>([]);
  const numLasers = useRef(1);

  // Changed from 1/2 screen height to 3/4 screen height
  const centerY = useRef(window.innerHeight * 0.75 - HITBOX_SIZE / 2);

  const spherePos = useRef({ x: window.innerWidth / 2 - BALL_SIZE / 2, y: centerY.current });
  const sphereVelocity = useRef(0);

  const laserData = useRef<
    { x: number; y: number; hit: boolean; scored: boolean }[]
  >([{ x: window.innerWidth, y: centerY.current - 20, hit: false, scored: false }]);

  const hasJumped = useRef(false);
  const scoreRef = useRef(0);

  // Hold logic
  const isHolding = useRef(false);
  const holdStartTime = useRef(0);

  useEffect(() => {
    const interval = setInterval(() => setScore(scoreRef.current), 200);
    return () => clearInterval(interval);
  }, []);

  const handlePressStart = () => {
    if (!hasJumped.current) {
      sphereVelocity.current = BOOST;
      hasJumped.current = true;
    }
    isHolding.current = true;
    holdStartTime.current = performance.now();
  };

  const handlePressEnd = () => {
    isHolding.current = false;
  };

  useEffect(() => {
    const loop = () => {
      // Adjust number of lasers based on score
      if (scoreRef.current >= 100) numLasers.current = 3;
      else if (scoreRef.current >= 50) numLasers.current = 2;
      else numLasers.current = 1;

      // Laser speed ramp-up
      const sectionScore = scoreRef.current % 50;
      const currentLaserSpeed =
        BASE_LASER_SPEED + LASER_SPEED_INCREMENT * Math.floor(sectionScore / 10);

      // Gravity pull
      sphereVelocity.current -= GRAVITY;

      // Hold logic — active immediately on press
      if (isHolding.current) {
        const heldTime = performance.now() - holdStartTime.current;
        if (heldTime < MAX_HOLD_TIME) {
          sphereVelocity.current += HOLD_BOOST;
        } else {
          isHolding.current = false;
        }
      }

      // Update position
      spherePos.current.y -= sphereVelocity.current;

      // Top boundary
      if (spherePos.current.y < 0) {
        spherePos.current.y = 0;
        sphereVelocity.current = -sphereVelocity.current * ENERGY_LOSS;
      }

      // Ground collision (now ¾ screen)
