  // Apply bounce effect
  sphereVelocity.current = -sphereVelocity.current * ENERGY_LOSS;

  // Stop micro-bounces when nearly still
  if (Math.abs(sphereVelocity.current) < 0.3) {
    sphereVelocity.current = 0;
  }
}



      // Maintain correct number of lasers
      while (laserData.current.length < numLasers.current) {
        const offset = (window.innerWidth / numLasers.current) * laserData.current.length;
        laserData.current.push({
          x: window.innerWidth + offset,
          y: centerY.current - 20,
          hit: false,
          scored: false,
        });
      }

      while (laserData.current.length > numLasers.current) {
        laserData.current.pop();
      }

      // Laser updates
      laserData.current.forEach((laser, i) => {
        const s = spherePos.current;
        laser.x -= currentLaserSpeed;

        // Collision detection
        if (
          !laser.hit &&
          s.x + BALL_SIZE > laser.x &&
          s.x < laser.x + LASER_WIDTH &&
          s.y + BALL_SIZE > laser.y &&
          s.y < laser.y + LASER_HEIGHT
        ) {
          laser.hit = true;
          if (sphereRef.current) sphereRef.current.style.backgroundColor = 'red';
        }

        // Score if passed safely
        if (!laser.scored && !laser.hit && laser.x + LASER_WIDTH < s.x) {
          scoreRef.current += 1;
          laser.scored = true;
          if (sphereRef.current) sphereRef.current.style.backgroundColor = '#4fc3f7';
        }

        // Reset offscreen lasers
        if (laser.x + LASER_WIDTH < 0) {
          laser.x = window.innerWidth;
          laser.hit = false;
          laser.scored = false;
        }

        if (lasersRef.current[i])
          lasersRef.current[i].style.transform = `translate3d(${laser.x}px, ${laser.y}px, 0)`;
      });

      // Update sphere position
      if (sphereRef.current)
        sphereRef.current.style.transform = `translate3d(${spherePos.current.x}px, ${spherePos.current.y}px, 0)`;

      requestAnimationFrame(loop);
    };

    requestAnimationFrame(loop);
  }, []);

  // Resize event (keeps center ¾ down on window resize)
  useEffect(() => {
    const handleResize = () => {
      centerY.current = window.innerHeight * 0.75 - HITBOX_SIZE / 2;
      spherePos.current.y = centerY.current;
      laserData.current.forEach((laser) => (laser.y = centerY.current - 20));
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Controls
  useEffect(() => {
    const events = [
      ['mousedown', handlePressStart],
      ['mouseup', handlePressEnd],
      ['touchstart', handlePressStart],
      ['touchend', handlePressEnd],
      ['keydown', handlePressStart],
      ['keyup', handlePressEnd],
    ] as const;

    for (const [event, handler] of events) window.addEventListener(event, handler);
    return () => {
      for (const [event, handler] of events) window.removeEventListener(event, handler);
    };
  }, []);

  return (
    <div style={{ position: 'fixed', inset: 0, overflow: 'hidden', background: '#000' }}>
      <div
        style={{
          position: 'absolute',
          top: 20,
          width: '100%',
          textAlign: 'center',
          fontSize: '2rem',
          fontWeight: 'bold',
          color: 'white',
        }}
      >
        Score: {score}
      </div>

      <div
        ref={sphereRef}
        style={{
          position: 'absolute',
          width: `${BALL_SIZE}px`,
          height: `${BALL_SIZE}px`,
          borderRadius: '50%',
          backgroundColor: '#4fc3f7',
        }}
      />

      {Array.from({ length: numLasers.current }).map((_, i) => (
        <div
          key={i}
          ref={(el) => {
            if (el) lasersRef.current[i] = el;
          }}
          style={{
            position: 'absolute',
            width: `${LASER_WIDTH}px`,
            height: `${LASER_HEIGHT}px`,
            backgroundColor: 'red',
            boxShadow: '0 0 10px red',
          }}
        />
      ))}
    </div>
  );
};

export default App;
