import React, { useRef, useEffect, useState } from 'react';

const BALL_SIZE = 80;
const HITBOX_SIZE = BALL_SIZE + 20;

// Physics
const GRAVITY = 0.42;
const ENERGY_LOSS = 0.55;
const BOOST = 15.35;
const HOLD_BOOST = 0.16;
const MAX_HOLD_TIME = 2000;

// Lasers
const LASER_WIDTH = 25;
const LASER_HEIGHT = 2;
const BASE_LASER_SPEED = 5;
const LASER_SPEED_INCREMENT = 3;

const App: React.FC = () => {
  const [score, setScore] = useState(0);

  const sphereRef = useRef<HTMLDivElement>(null);
  const lasersRef = useRef<HTMLDivElement[]>([]);
  const laserData = useRef<{ x: number; y: number; hit: boolean; scored: boolean; direction: 1 | -1 }[]>([]);
  const numLasers = useRef(1);
  const laserSpeed = useRef(BASE_LASER_SPEED);

  const centerY = useRef(window.innerHeight * 0.75 - HITBOX_SIZE / 2);
  const spherePos = useRef({ x: window.innerWidth / 2 - BALL_SIZE / 2, y: centerY.current });
  const sphereVelocity = useRef(0);

  const hasJumped = useRef(false);
  const scoreRef = useRef(0);
  const isHolding = useRef(false);
  const holdStartTime = useRef(0);

  // Fluid animation scales
  const scaleX = useRef(1);
  const scaleY = useRef(1);

  // Score updater
  useEffect(() => {
    const interval = setInterval(() => setScore(scoreRef.current), 200);
    return () => clearInterval(interval);
  }, []);

  const handlePressStart = () => {
    if (!hasJumped.current) {
      sphereVelocity.current = BOOST;
      hasJumped.current = true;
    }
    isHolding.current = true;
    holdStartTime.current = performance.now();
  };

  const handlePressEnd = () => {
    isHolding.current = false;
  };

  useEffect(() => {
    const loop = () => {
      // Determine number of lasers based on score
      numLasers.current = Math.floor(scoreRef.current / 25) + 1;

      // Reset laser speed every 25 points
      if (scoreRef.current % 25 === 0 && scoreRef.current !== 0) {
        laserSpeed.current = BASE_LASER_SPEED;
      }

      // Ensure laserData has the right number of lasers
      while (laserData.current.length < numLasers.current) {
        const direction: 1 | -1 = (scoreRef.current >= 100 && laserData.current.length % 2 === 1) ? -1 : 1;
        const spacing = window.innerWidth / numLasers.current;
        const xStart = direction === 1 ? window.innerWidth + laserData.current.length * spacing : -LASER_WIDTH - laserData.current.length * spacing;
        laserData.current.push({ x: xStart, y: centerY.current, hit: false, scored: false, direction });
      }
      while (laserData.current.length > numLasers.current) laserData.current.pop();

      const sectionScore = scoreRef.current % 50;
      const currentLaserSpeed = laserSpeed.current + LASER_SPEED_INCREMENT * Math.floor(sectionScore / 5);

      // Sphere physics
      sphereVelocity.current -= GRAVITY;
      if (isHolding.current) {
        const heldTime = performance.now() - holdStartTime.current;
        if (heldTime < MAX_HOLD_TIME) sphereVelocity.current += HOLD_BOOST;
        else isHolding.current = false;
      }
      spherePos.current.y -= sphereVelocity.current;

      // Boundaries
      if (spherePos.current.y < 0) {
        spherePos.current.y = 0;
        sphereVelocity.current = -sphereVelocity.current * ENERGY_LOSS;
      }
      if (spherePos.current.y > centerY.current) {
        spherePos.current.y = centerY.current;
        sphereVelocity.current = -sphereVelocity.current * ENERGY_LOSS;
        hasJumped.current = false;
        if (Math.abs(sphereVelocity.current) < 0.3) sphereVelocity.current = 0;
      }

      // Update lasers
      const spacing = window.innerWidth / numLasers.current;
      laserData.current.forEach((laser, i) => {
        laser.x += currentLaserSpeed * laser.direction;

        // Reset laser if offscreen
        if ((laser.direction === 1 && laser.x + LASER_WIDTH < 0) || (laser.direction === -1 && laser.x > window.innerWidth)) {
          laser.x = laser.direction === 1 ? window.innerWidth + i * spacing : -LASER_WIDTH - i * spacing;
          laser.hit = false;
          laser.scored = false;
          laser.y = Math.random() * (centerY.current - window.innerHeight * 0.5) + window.innerHeight * 0.5;
        }

        // Collision detection
        const s = spherePos.current;
        if (!laser.hit && s.x + BALL_SIZE > laser.x && s.x < laser.x + LASER_WIDTH && s.y + BALL_SIZE > laser.y && s.y < laser.y + LASER_HEIGHT) {
          laser.hit = true;
          if (sphereRef.current) {
            // Immediate red without transition
            sphereRef.current.style.transition = 'none';
            sphereRef.current.style.backgroundColor = 'red';
            // After 0.25s, start fading back
            setTimeout(() => {
              if (sphereRef.current) {
                sphereRef.current.style.transition = 'background-color .75s ease';
                sphereRef.current.style.backgroundColor = '#4fc3f7';
              }
            }, 250);
          }
        }

        if (!laser.scored && !laser.hit && ((laser.direction === 1 && laser.x + LASER_WIDTH < s.x) || (laser.direction === -1 && laser.x > s.x + BALL_SIZE))) {
          laser.scored = true;
          scoreRef.current += 1;
        }

        if (lasersRef.current[i]) lasersRef.current[i].style.transform = `translate3d(${laser.x}px, ${laser.y}px, 0)`;
      });

      // Fluid squash/stretch
      let targetScaleX = 1;
      let targetScaleY = 1;
      if (Math.abs(sphereVelocity.current) > 0.1) {
        if (sphereVelocity.current > 0) {
          targetScaleY = 1 - sphereVelocity.current / 50;
          targetScaleX = 1 + sphereVelocity.current / 50;
        } else {
          targetScaleY = 1 + Math.abs(sphereVelocity.current) / 50;
          targetScaleX = 1 - Math.abs(sphereVelocity.current) / 50;
        }
      }
      if (spherePos.current.y >= centerY.current && Math.abs(sphereVelocity.current) < 0.5) {
        targetScaleY = 0.7;
        targetScaleX = 1.3;
      }
      if (Math.abs(sphereVelocity.current) < 0.01 && spherePos.current.y >= centerY.current) {
        targetScaleX = 1;
        targetScaleY = 1;
      }
      scaleX.current += (targetScaleX - scaleX.current) * 0.15;
      scaleY.current += (targetScaleY - scaleY.current) * 0.15;
      if (sphereRef.current)
        sphereRef.current.style.transform = `translate3d(${spherePos.current.x}px, ${spherePos.current.y}px, 0) scale(${scaleX.current}, ${scaleY.current})`;

      requestAnimationFrame(loop);
    };

    requestAnimationFrame(loop);
  }, []);

  // Window resize
  useEffect(() => {
    const handleResize = () => {
      centerY.current = window.innerHeight * 0.75 - HITBOX_SIZE / 2;
      spherePos.current.y = centerY.current;
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Controls
  useEffect(() => {
    const events = [
      ['mousedown', handlePressStart],
      ['mouseup', handlePressEnd],
      ['touchstart', handlePressStart],
      ['touchend', handlePressEnd],
      ['keydown', handlePressStart],
      ['keyup', handlePressEnd],
    ] as const;
    for (const [event, handler] of events) window.addEventListener(event, handler);
    return () => {
      for (const [event, handler] of events) window.removeEventListener(event, handler);
    };
  }, []);

  return (
    <div style={{ position: 'fixed', inset: 0, overflow: 'hidden', background: '#000' }}>
      <div style={{ position: 'absolute', top: 20, width: '100%', textAlign: 'center', fontSize: '2rem', fontWeight: 'bold', color: 'white' }}>
        Score: {score}
      </div>

      <div
        ref={sphereRef}
        style={{
          position: 'absolute',
          width: `${BALL_SIZE}px`,
          height: `${BALL_SIZE}px`,
          borderRadius: '50%',
          backgroundColor: '#4fc3f7',
          transform: 'scale(1,1)', // start perfectly round
        }}
      />

      {Array.from({ length: laserData.current.length }).map((_, i) => (
        <div
          key={i}
          ref={(el) => {
            if (el) lasersRef.current[i] = el;
          }}
          style={{ position: 'absolute', width: `${LASER_WIDTH}px`, height: `${LASER_HEIGHT}px`, backgroundColor: 'red', boxShadow: '0 0 10px red' }}
        />
      ))}
    </div>
  );
};

export default App;
