import React, { useRef, useEffect, useState } from 'react';
import './App.css';

const GRAVITY = 0.7;
const ENERGY_LOSS = 0.7;
const BOOST = 18;
const BALL_SIZE = 80;
const HITBOX_SIZE = BALL_SIZE + 20;
const LASER_WIDTH = 25;
const LASER_HEIGHT = 1;
const LASER_SPEED = 8;

const App: React.FC = () => {
  const [isBouncing, setIsBouncing] = useState(false);
  const [score, setScore] = useState(0);
  const [sphereColor, setSphereColor] = useState('#4fc3f7');
  const [hasJumped, setHasJumped] = useState(false);
  const sphereRef = useRef<HTMLDivElement>(null);
  const hitboxRef = useRef<HTMLDivElement>(null);
  const velocityRef = useRef(0);
  const centerRef = useRef(window.innerHeight / 2 - HITBOX_SIZE / 2);
  const positionRef = useRef(centerRef.current);
  const animationRef = useRef<number>();
  const boundsRef = useRef({
    top: 0,
    center: centerRef.current,
  });

  // Laser state
  const [laserX, setLaserX] = useState(window.innerWidth);
  const [laserActive, setLaserActive] = useState(true);
  const laserYRef = useRef(centerRef.current - 20); // 20px above center
  const laserAnimationRef = useRef<number>();
  const [laserHit, setLaserHit] = useState(false);

  // Update bounds on resize
  useEffect(() => {
    const handleResize = () => {
      centerRef.current = window.innerHeight / 2 - HITBOX_SIZE / 2;
      boundsRef.current = {
        top: 0,
        center: centerRef.current,
      };
      positionRef.current = centerRef.current;
      laserYRef.current = centerRef.current - 20;
      if (hitboxRef.current) {
        hitboxRef.current.style.top = `${positionRef.current}px`;
      }
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Start in middle
  useEffect(() => {
    if (hitboxRef.current) {
      hitboxRef.current.style.top = `${positionRef.current}px`;
    }
  }, []);

  // Bounce logic
  const animate = () => {
    animationRef.current = requestAnimationFrame(() => {
      velocityRef.current -= GRAVITY;
      positionRef.current -= velocityRef.current;

      // Bounce off the top
      if (positionRef.current < boundsRef.current.top) {
        positionRef.current = boundsRef.current.top;
        velocityRef.current = -velocityRef.current * ENERGY_LOSS;
      }

      // Bounce off the center (never below center)
      if (positionRef.current > boundsRef.current.center) {
        positionRef.current = boundsRef.current.center;
        velocityRef.current = -velocityRef.current * ENERGY_LOSS;
        // Stop if velocity is very low
        if (Math.abs(velocityRef.current) < 1.5) {
          velocityRef.current = 0;
          setIsBouncing(false);
          if (hitboxRef.current) {
            hitboxRef.current.style.top = `${positionRef.current}px`;
          }
          return;
        }
      }

      if (hitboxRef.current) {
        hitboxRef.current.style.top = `${positionRef.current}px`;
      }

      animate();
    });
  };

  // Always set velocity to BOOST on click
  const handleHitboxClick = () => {
    velocityRef.current = BOOST;
    setHasJumped(true);
    if (!isBouncing) {
      setIsBouncing(true);
      animate();
    }
  };

  // Laser animation
  useEffect(() => {
    if (!laserActive) return;
    let hit = false;
    const animateLaser = () => {
      setLaserX(prevX => {
        const nextX = prevX - LASER_SPEED;
        // Collision detection
        if (sphereRef.current) {
          const sphereRect = sphereRef.current.getBoundingClientRect();
          const sphereTop = sphereRect.top;
          const sphereBottom = sphereRect.bottom;
          const sphereLeft = sphereRect.left;
          const sphereRight = sphereRect.right;

          const laserTop = laserYRef.current;
          const laserBottom = laserYRef.current + LASER_HEIGHT;
          const laserLeft = nextX;
          const laserRight = nextX + LASER_WIDTH;

          // Check if laser overlaps with sphere
          if (
            laserRight > sphereLeft &&
            laserLeft < sphereRight &&
            laserTop < sphereBottom &&
            laserBottom > sphereTop &&
            !hit
          ) {
            setSphereColor('red');
            setLaserHit(true);
            hit = true;
          }
        }

        // If laser is off screen
        if (nextX + LASER_WIDTH < 0) {
          // Only increment score if player jumped and did not get hit
          if (!hit && !laserHit && hasJumped) {
            setScore(s => s + 1);
          }
          setLaserX(window.innerWidth);
          setLaserHit(false);
          setSphereColor('#4fc3f7');
          setHasJumped(false); // Reset jump for next laser
          hit = false;
          return window.innerWidth;
        }
        return nextX;
      });
      laserAnimationRef.current = requestAnimationFrame(animateLaser);
    };
    laserAnimationRef.current = requestAnimationFrame(animateLaser);
    return () => {
      if (laserAnimationRef.current) cancelAnimationFrame(laserAnimationRef.current);
    };
    // eslint-disable-next-line
  }, [laserActive, laserHit, hasJumped]);

  // Cleanup animation frame
  useEffect(() => {
    return () => {
      if (animationRef.current) cancelAnimationFrame(animationRef.current);
      if (laserAnimationRef.current) cancelAnimationFrame(laserAnimationRef.current);
    };
  }, []);

  return (
    <div style={{ position: 'fixed', inset: 0, overflow: 'hidden' }}>
      <div style={{
        position: 'absolute',
        top: 20,
        left: 0,
        width: '100%',
        textAlign: 'center',
        fontSize: '2rem',
        fontWeight: 'bold',
        color: '#333'
      }}>
        Score: {score}
      </div>
      <div
        ref={hitboxRef}
        className="hitbox"
        style={{
          position: 'absolute',
          left: `calc(50% - ${HITBOX_SIZE / 2}px)`,
          top: `${centerRef.current}px`,
          width: `${HITBOX_SIZE}px`,
          height: `${HITBOX_SIZE}px`,
          cursor: 'pointer',
        }}
        onClick={handleHitboxClick}
      >
        <div
          ref={sphereRef}
          className="sphere"
          style={{
            position: 'absolute',
            left: '10px',
            top: '10px',
            background: sphereColor === 'red'
              ? 'radial-gradient(circle at 30% 30%, #ff5252 70%, #b71c1c 100%)'
              : 'radial-gradient(circle at 30% 30%, #4fc3f7 70%, #0288d1 100%)',
            transition: 'background 0.2s',
          }}
        />
      </div>
      {/* Laser beam */}
      <div
        className="laser"
        style={{
          position: 'absolute',
          left: `${laserX}px`,
          top: `${laserYRef.current}px`,
          width: `${LASER_WIDTH}px`,
          height: `${LASER_HEIGHT}px`,
          background: 'red',
          boxShadow: '0 0 8px 2px red',
        }}
      />
    </div>
  );
};

export default App;
