/**
 * Enemy Component
 * Renders the enemy launcher ball
 */

import React from 'react';
import { BALL_SIZE } from '../config/gameConfig';

interface EnemyProps {
  x: number;
  y: number;
}

export const Enemy = React.forwardRef<HTMLDivElement, EnemyProps>(
  ({ x, y }, ref) => {
    return (
      <div
        ref={ref}
        style={{
          position: 'absolute',
          width: `${BALL_SIZE}px`,
          height: `${BALL_SIZE}px`,
          borderRadius: '50%',
          backgroundColor: 'red',
          boxShadow: '0 0 15px red',
          transform: `translate3d(${x}px, ${y}px, 0)`,
        }}
      />
    );
  }
);

Enemy.displayName = 'Enemy';
